      console.log('mutations中的JIAN被调用了',state, value)
      state.sum -= value
    }
  },
  getters: {
    bigSum(state){
      return state.sum*10
    }
  }

}

// 人员管理相关配置
const personOptions = {
  actions:{
    Add_Person(state, value){
