import Vue from 'vue'
import App from './App.vue'

import vueResource from 'vue-resource'
// 引入vuex
import Vuex from 'vuex'
// 引入store
import store from './store' // 默认导出的是./store/index.js

Vue.config.productionTip = false

Vue.use(vueResource)
// Vue.use(Vuex)
new Vue({
  render: h => h(App),
  store,
  beforeCreate() {
    Vue.prototype.$bus = this // 安装全局事件总线
  },
}).$mount('#app')
