// 该文件用于创建Vuex中最核心的store

// 引入Vue
import Vue from 'vue'
// 引入vuex
import Vuex from 'vuex'

// 准备actions--用于响应组件中的动作
const actions = {
  jia(context,value){
    console.log('actions中的加被调用了')
    context.commit('jia')
  }
}

// 准备mutations--用于操作数据(state)
const mutations = {}

// 准备state--用于存储数据
const state = {
  sum:0 // 当前的和
}

Vue.use(Vuex)
// 创建并暴露store
export default new Vuex.Store({
  actions,
  mutations,
  state,
})
