import Vue from 'vue'
import App from './App.vue'

import vueResource from 'vue-resource'
// 引入vuex
import Vuex from 'vuex'

Vue.config.productionTip = false

Vue.use(vueResource)
Vue.user(Vuex)
new Vue({
  render: h => h(App),
  beforeCreate() {
    Vue.prototype.$bus = this // 安装全局事件总线
  },
}).$mount('#app')
