import "core-js/modules/es.array.unshift.js";
import todosHeader from './components/todosHeader';
import todosList from './components/todosList';
import todosFooter from './components/todosFooter';
import eventBus from './config/constans';
export default {
  name: 'App',
  components: {
    todosHeader,
    todosList,
    todosFooter
  },
  data() {
    return {
      todos: JSON.parse(localStorage.getItem('todos')) || []
      // todos: []
    };
  },

  methods: {
    // 添加一个todo
    addTodo(todoObj) {
      console.log('App收到了子组件Header传过来的要添加的todo对象', todoObj);
      this.todos.unshift(todoObj);
    },
    // 勾选或者取消勾选
    checkTodo(id) {
      console.log('App收到了子组件Item传过来的是否要勾选的checkObj对象', id);
      this.todos.forEach(todo => {
        if (todo.id == id) {
          // 原来的值取反，赋值回去就可以了
          todo.done = !todo.done;
        }
      });
    },
    // 删除一个todo
    deleteTodo(id) {
      console.log('App收到了子组件Item传过来的要删除的todo的id', id);
      this.todos = this.todos.filter(todo => todo.id !== id);
    },
    // 全选或者取消全选
    checkAllTodo(done) {
      console.log('App收到了子组件footer传过来的是否要全选或者取消全选', done);
      this.todos.forEach(todo => {
        todo.done = done;
      });
    },
    // 清除已完成任务
    clearAlltodo() {
      this.todos = this.todos.filter(todo => {
        return !todo.done;
      });
    }
  },
  mounted() {
    this.$on.$bus(eventBus.checkTodo, id);
    this.$on.$bus(eventBus.deleteTodo, id);
  },
  beforeDestroy() {
    this.$off.$bus(eventBus.checkTodo);
    this.$off.$bus(eventBus.deleteTodo);
  },
  watch: {
    todos: {
      immediate: true,
      deep: true,
      handler(value) {
        localStorage.setItem('todos', JSON.stringify(value));
      }
    }
  }
};
